// utils/taskUtils.js

// Llaves para localStorage
const MEMBERS_KEY = 'familytask_members';
const TASKS_KEY = 'familytask_tasks';

// Carga miembros desde localStorage
export function loadMembers() {
  const data = localStorage.getItem(MEMBERS_KEY);
  return data ? JSON.parse(data) : [];
}

// Guarda miembros en localStorage
export function saveMembers(members) {
  localStorage.setItem(MEMBERS_KEY, JSON.stringify(members));
}

// Carga tareas desde localStorage
export function loadTasks() {
  const data = localStorage.getItem(TASKS_KEY);
  return data ? JSON.parse(data) : [];
}

// Guarda tareas en localStorage
export function saveTasks(tasks) {
  localStorage.setItem(TASKS_KEY, JSON.stringify(tasks));
}

// Rota asignación y actualiza la fecha de la tarea completada
export function rotateAssignment(tasks, taskId, members) {
  return tasks.map(task => {
    if (task.id === taskId) {
      const currentStartDate = new Date(task.startDate);

      // Suma días de recurrencia
      const newStartDate = new Date(currentStartDate);
      newStartDate.setDate(currentStartDate.getDate() + Number(task.recurrenceDays));

      // Encuentra índice actual del miembro asignado
      const currentMemberIndex = members.findIndex(m => m.id === task.assignedTo);

      // Si no encuentra, asigna al primer miembro
      const nextMemberIndex = currentMemberIndex === -1 ? 0 : (currentMemberIndex + 1) % members.length;

      return {
        ...task,
        assignedTo: members[nextMemberIndex]?.id || null,
        startDate: newStartDate.toISOString().split('T')[0], // formato YYYY-MM-DD
      };
    }
    return task;
  });
}
