import React, { useState, useEffect } from 'react';
import MemberForm from './components/MemberForm';
import MemberList from './components/MemberList';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import NotificationManager from './components/NotificationManager';
import {
  loadMembers,
  saveMembers,
  loadTasks,
  saveTasks,
  rotateAssignment
} from './utils/taskUtils';

const App = () => {
  const [members, setMembers] = useState(() => loadMembers());
  const [tasks, setTasks] = useState(() => loadTasks());
  const [editingMember, setEditingMember] = useState(null);
  const [editingTask, setEditingTask] = useState(null);
  const [newTaskId, setNewTaskId] = useState(null);
  const [deleteTaskId, setDeleteTaskId] = useState(null);

  useEffect(() => {
    saveMembers(members);
  }, [members]);

  useEffect(() => {
    saveTasks(tasks);
  }, [tasks]);

  const handleAddMember = (newMember) => {
    setMembers([...members, newMember]);
  };

  const handleUpdateMember = (updatedMember) => {
    const updated = members.map(m =>
      m.id === updatedMember.id ? updatedMember : m
    );
    setMembers(updated);
    setEditingMember(null);
  };

  const handleDeleteMember = (memberId) => {
    if (
      tasks.some(task => task.assignedTo === memberId) &&
      !window.confirm(
        'Este miembro tiene tareas asignadas. ¿Eliminar de todas formas?'
      )
    ) {
      return;
    }

    const updatedMembers = members.filter(m => m.id !== memberId);
    const updatedTasks = tasks.map(task =>
      task.assignedTo === memberId ? { ...task, assignedTo: null } : task
    );
    setMembers(updatedMembers);
    setTasks(updatedTasks);
  };

  const handleAddTask = (newTask) => {
    const taskWithId = {
      ...newTask,
      id: tasks.length > 0 ? Math.max(...tasks.map(t => t.id)) + 1 : 1
    };
    setTasks([...tasks, taskWithId]);
    setNewTaskId(taskWithId.id);
  };

  const handleUpdateTask = (updatedTask) => {
    const updatedTasks = tasks.map(t =>
      t.id === updatedTask.id ? updatedTask : t
    );
    setTasks(updatedTasks);
    setEditingTask(null);
  };

  const handleDeleteTask = (taskId) => {
    const updatedTasks = tasks.filter(task => task.id !== taskId);
    setTasks(updatedTasks);
    setDeleteTaskId(taskId);
  };

  const handleCompleteTask = (taskId) => {
    const updatedTasks = rotateAssignment(tasks, taskId, members);
    setTasks(updatedTasks);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8">
      <div className="max-w-3xl mx-auto space-y-6">
        <h1 className="text-3xl font-bold text-gray-800">FamilyTask Pro</h1>
        <p className="text-gray-600">Gestión completa de tareas domésticas</p>

        <MemberForm
          onAddMember={handleAddMember}
          editingMember={editingMember}
          onUpdateMember={handleUpdateMember}
        />

        <MemberList
          members={members}
          onEditMember={setEditingMember}
          onDeleteMember={handleDeleteMember}
        />

        {members.length > 0 ? (
          <>
            <TaskForm
              members={members}
              onAddTask={handleAddTask}
              editingTask={editingTask}
              onUpdateTask={handleUpdateTask}
            />

            <TaskList
              members={members}
              tasks={tasks}
              onCompleteTask={handleCompleteTask}
              onDeleteTask={handleDeleteTask}
              onEditTask={setEditingTask}
            />

            <NotificationManager
              members={members}
              tasks={tasks}
              newTaskId={newTaskId}
              onDeleteTaskId={deleteTaskId}
            />
          </>
        ) : (
          <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4">
            <p className="text-yellow-700">
              Agrega al menos un miembro del hogar para comenzar a crear tareas
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
