import React, { useState } from 'react';

const TaskForm = ({ members, onAddTask }) => {
  const [title, setTitle] = useState('');
  const [assignedTo, setAssignedTo] = useState('');
  const [startDate, setStartDate] = useState('');
  const [recurrenceDays, setRecurrenceDays] = useState(7);
  const [notificationMethods, setNotificationMethods] = useState({
    whatsapp: true,
    email: true
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const newTask = {
      title,
      assignedTo: parseInt(assignedTo),
      startDate,
      recurrenceDays,
      notificationMethods: {
        whatsapp: notificationMethods.whatsapp,
        email: notificationMethods.email
      }
    };
    onAddTask(newTask);
    resetForm();
  };

  const resetForm = () => {
    setTitle('');
    setAssignedTo('');
    setStartDate('');
    setRecurrenceDays(7);
  };

  const handleNotificationChange = (method) => {
    setNotificationMethods(prev => ({
      ...prev,
      [method]: !prev[method]
    }));
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4">Nueva Tarea Recurrente</h2>
      <div className="mb-4">
        <label className="block text-gray-700 mb-2">Tarea</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 mb-2">Asignar a</label>
        <select
          value={assignedTo}
          onChange={(e) => setAssignedTo(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          required
        >
          <option value="">Seleccionar persona</option>
          {members.map(member => (
            <option key={member.id} value={member.id}>{member.name}</option>
          ))}
        </select>
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 mb-2">Fecha de inicio</label>
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 mb-2">Recurrencia (días)</label>
        <select
          value={recurrenceDays}
          onChange={(e) => setRecurrenceDays(parseInt(e.target.value))}
          className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          required
        >
          {Array.from({ length: 30 }, (_, i) => i + 1).map(num => (
            <option key={num} value={num}>{num}</option>
          ))}
        </select>
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 mb-2">Métodos de notificación</label>
        <div className="flex space-x-4">
          <label className="inline-flex items-center">
            <input
              type="checkbox"
              checked={notificationMethods.whatsapp}
              onChange={() => handleNotificationChange('whatsapp')}
              className="text-blue-500"
            />
            <span className="ml-2">WhatsApp</span>
          </label>
          <label className="inline-flex items-center">
            <input
              type="checkbox"
              checked={notificationMethods.email}
              onChange={() => handleNotificationChange('email')}
              className="text-blue-500"
            />
            <span className="ml-2">Email</span>
          </label>
        </div>
      </div>
      <button
        type="submit"
        className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition-colors"
      >
        Crear Tarea
      </button>
    </form>
  );
};

export default TaskForm;
