// utils/taskUtils.js

const MEMBERS_KEY = 'familyTask_members';
const TASKS_KEY = 'familyTask_tasks';

export const loadMembers = () => {
  const data = localStorage.getItem(MEMBERS_KEY);
  return data ? JSON.parse(data) : [];
};

export const saveMembers = (members) => {
  localStorage.setItem(MEMBERS_KEY, JSON.stringify(members));
};

export const loadTasks = () => {
  const data = localStorage.getItem(TASKS_KEY);
  return data ? JSON.parse(data) : [];
};

export const saveTasks = (tasks) => {
  localStorage.setItem(TASKS_KEY, JSON.stringify(tasks));
};

// Rota la asignación de una tarea al siguiente miembro en la lista
export const rotateAssignment = (tasks, taskId, members) => {
  return tasks.map(task => {
    if (task.id === taskId) {
      const currentIndex = members.findIndex(m => m.id === task.assignedTo);
      const nextIndex = (currentIndex + 1) % members.length;
      return {
        ...task,
        assignedTo: members[nextIndex].id,
        startDate: new Date().toISOString().split('T')[0] // nuevo inicio desde hoy
      };
    }
    return task;
  });
};
