import React, { useState, useEffect } from 'react';
import MemberForm from './components/MemberForm';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import NotificationManager from './components/NotificationManager';
import { loadMembers, saveMembers, loadTasks, saveTasks, rotateAssignment } from './utils/taskUtils';

const App = () => {
  const [members, setMembers] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [newTaskId, setNewTaskId] = useState(null);
  const [deleteTaskId, setDeleteTaskId] = useState(null);

  // Load members and tasks from localStorage on initial render
  useEffect(() => {
    setMembers(loadMembers());
    setTasks(loadTasks());
  }, []);

  // Save members to localStorage whenever the members state changes
  useEffect(() => {
    saveMembers(members);
  }, [members]);

  // Save tasks to localStorage whenever the tasks state changes
  useEffect(() => {
    saveTasks(tasks);
  }, [tasks]);

  /**
   * Handles adding a new member to the list.
   * @param {object} newMember - The new member object to add.
   */
  const handleAddMember = (newMember) => {
    // Generate a unique ID for the new member
    const updatedMembers = [...members, { ...newMember, id: Date.now() }];
    setMembers(updatedMembers);
  };

  /**
   * Handles adding a new task to the list.
   * Generates a new ID for the task and updates the state.
   * @param {object} newTask - The new task object to add.
   */
  const handleAddTask = (newTask) => {
    // Generate a new ID for the task based on existing task IDs
    const taskWithId = {
      ...newTask,
      id: tasks.length > 0 ? Math.max(...tasks.map(t => t.id)) + 1 : 1
    };
    const updatedTasks = [...tasks, taskWithId];
    setTasks(updatedTasks);
    // Set newTaskId to trigger notification for the newly added task
    setNewTaskId(taskWithId.id);
  };

  /**
   * Handles completing a task. This will rotate the assignment of the task.
   * @param {number} taskId - The ID of the task to complete.
   */
  const handleCompleteTask = (taskId) => {
    const updatedTasks = rotateAssignment(tasks, taskId, members);
    setTasks(updatedTasks);
  };

  /**
   * Handles deleting a task from the list.
   * @param {number} taskId - The ID of the task to delete.
   */
  const handleDeleteTask = (taskId) => {
    const updatedTasks = tasks.filter(task => task.id !== taskId);
    setTasks(updatedTasks);
    // Set deleteTaskId to trigger any necessary notification or action
    setDeleteTaskId(taskId);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8 font-sans">
      <div className="max-w-3xl mx-auto space-y-6">
        <h1 className="text-4xl font-extrabold text-gray-800 text-center mb-6">FamilyTask Pro</h1>
        <p className="text-lg text-gray-600 text-center mb-8">Gestión completa de tareas domésticas para tu hogar</p>

        {/* Member Form */}
        <MemberForm onAddMember={handleAddMember} />

        {/* Conditional rendering based on members availability */}
        {members.length > 0 ? (
          <>
            {/* Task Form */}
            <TaskForm members={members} onAddTask={handleAddTask} />

            {/* Task List */}
            <TaskList
              members={members}
              tasks={tasks}
              onCompleteTask={handleCompleteTask}
              onDeleteTask={handleDeleteTask}
            />

            {/* Notification Manager */}
            <NotificationManager
              members={members}
              tasks={tasks}
              newTaskId={newTaskId}
              onDeleteTaskId={deleteTaskId}
            />
          </>
        ) : (
          // Message to add members if none exist
          <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-800 p-4 rounded-lg shadow-sm">
            <p className="font-medium">¡Atención!</p>
            <p>Agrega al menos un miembro del hogar para comenzar a crear tareas y organizar tu vida.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
