export const rotateAssignment = (tasks, taskId, members) => {
  return tasks.map(task => {
    if (task.id === taskId) {
      const currentIndex = members.findIndex(m => m.id === task.assignedTo);
      const nextIndex = (currentIndex + 1) % members.length;
      return {
        ...task,
        assignedTo: members[nextIndex].id,
        startDate: new Date(new Date(task.startDate).getTime() + task.recurrenceDays * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
      };
    }
    return task;
  });
};

export const saveMembers = (members) => {
  localStorage.setItem('householdMembers', JSON.stringify(members));
};

export const loadMembers = () => {
  const savedMembers = localStorage.getItem('householdMembers');
  return savedMembers ? JSON.parse(savedMembers) : [];
};

export const saveTasks = (tasks) => {
  localStorage.setItem('householdTasks', JSON.stringify(tasks));
};

export const loadTasks = () => {
  const savedTasks = localStorage.getItem('householdTasks');
  return savedTasks ? JSON.parse(savedTasks) : [];
};
